#if !defined(__SOFT_FP__) && defined(__ARM_FP)
#warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

#include "stdint.h"
//ÙŒRCC
#define RCC_BASE 0x40021000
#define RCC_APB2ENR *(volatile uint32_t *) (RCC_BASE + 0x18)
#define RCC_IOPAEN   (1<<2)

//GPIO
#define PORTA_BASE 0x40010800
#define GPIOA_CRH   *(volatile uint32_t *) (PORTA_BASE + 0x04)
#define GPIOx_CRL   *(volatile uint32_t *) (PORTA_BASE + 0x00)
#define GPIOA_ODR   *(volatile uint32_t *) (PORTA_BASE + 0x0C)
#define GPIOA13     (1UL<<13)

//EXTI
#define EXTI_BASE  0x40010400
#define EXTI_IMR   *(volatile uint32_t *) (EXTI_BASE + 0x00)
#define EXTI_RTSR   *(volatile uint32_t *) (EXTI_BASE + 0x08)
#define EXTI_PR   *(volatile uint32_t *) (EXTI_BASE + 0x14)
#define AFIO_BASE  0x40010000
#define AFIO_EXTICR1   *(volatile uint32_t *) (AFIO_BASE + 0x08)
#define NVIC_EXTIE0  *(volatile uint32_t *) (0xE0000000 + 0xE000E100)

void clock_init()
{
	//Enable clock for GPIOA
	RCC_APB2ENR |= RCC_IOPAEN;
	//Bit 0 AFIOEN: Alternate function IO clock enable
	RCC_APB2ENR |= (1<<0) ;
}

void GPIO_init()
{
	//pin 13 port A output
	GPIOA_CRH &= 0xff0fffff;
	GPIOA_CRH |= 0x00200000;
	//PIN A0 01: Floating input (reset state)
	GPIOx_CRL |= (1<<2) ;
}

int main(void)
{
	clock_init() ;
	GPIO_init() ;
	//EXTI0 configuration to portA0
	AFIO_EXTICR1=0;
	//Enable rising edge register
	EXTI_RTSR |=(1<<0) ;
	//Enable EXTI line 0 (Mask 1)
	EXTI_IMR|=(1<<0) ;
	//enable IRq6-->>EXTI0
	NVIC_EXTIE0 |=(1<<6) ;

	while(1) ;
}
void EXTI0_IRQHandler(void)
{
	//IRQ happened EXTI0 >>>portA PIN 0 _|- rising edge
	//toggle LED PIN A 13
	GPIOA_ODR ^=(1<<13) ;

	//Clear Pending Request for Line 0
	EXTI_PR|=(1<<0) ;
}



